import java.util.Scanner;

public class addSubMultiDiv1003 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m ;
		int n; 
		Scanner scan = new Scanner(System.in);
		m = scan.nextInt();
		n = scan.nextInt();
		System.out.print(m+n);
		System.out.print(" ");
		System.out.print(m-n);
		System.out.print(" ");
		System.out.print(m*n);
		System.out.print(" ");
		System.out.print(m/n);
		System.out.print(" ");
		System.out.print(m%n);
	}

}
